mapper.select:
SELECT *
FROM executor
WHERE ip = #{ip}
    AND port = #{port}

mapper.insert:
INSERT executor (
    id, executor_id, ip, port,
    name, token, status,
    keep_alive_time, register_time, update_time
)
VALUE (
    #{id}, #{executorId}, #{ip}, #{port},
    #{name}, #{token}, #{status},
    #{keepAliveTime}, #{registerTime}, #{updateTime}
)
